import styled from 'styled-components';
import { connect } from 'react-redux';
import { signInAPI } from '../actions';
import { Redirect } from 'react-router';

const Login = (props) => {
    return (
        <Container>
            {
                props.user && 
                <Redirect to = '/home' />
            }
            <Nav>
                <a href="/">
                    <img src="/images/login-logo.svg" alt="" />
                </a>
            </Nav>

            <Section>
                <Hero>
                    <h1>Login to painters blog</h1>
                </Hero>

                <Form>
                    <Google onClick = {() => props.signIn()}>
                        <img src="/images/google.svg" alt="" />continue with Google
                    </Google>
                </Form>
            </Section>
        </Container>
    );
};

const Container = styled.div`
    padding: 0px;
`;

const Nav = styled.nav`
    max-width: 1128px;
    margin: auto;
    padding: 12px 0 16px 0;
    display: flex;
    align-items: center;
    position: relative;
    justify-content: space-between;
    flex-wrap: nowrap;

    & > a {
        width: 135px;
        height: 34px;

        @media (max-width: 768px) {
            padding: 0 5px;
        }
    }
`;

const Join = styled.a`
    font-size: 16px;
    padding: 10px 12px;
    text-decoration: none;
    color: rgba(0, 0, 0, 0.6);
    margin-right: 12px;
    font-weight: 600;
    border-radius: 4px;

    &:hover {
        background-color: rgba(0, 0, 0, 0.08);
        color: rgba(0, 0, 0, 0.9);
        text-decoration: none;
        cursor: pointer;
    }
`;

const SignIn = styled.a`
    box-shadow: inset 0 0 0 1px #0a66c2;
    color: #0a66c2;
    border-radius: 24px;
    transition-duration: 167ms;
    font-size: 16px;
    font-weight: 600;
    line-height: 40px;
    padding: 12px 23px;
    text-align: center;
    background-color: rgba(0, 0, 0, 0);

    &:hover {
        background-color: rgba(112, 181, 249, 0.15);
        color: #0a66c2;
        text-decoration: none;
        cursor: pointer;
        box-shadow: inset 0 0 0 2px #0a66c2;
    }
`;

const Section = styled.section`
    display: flex;
    align-content: start;
    min-height: 700px;
    padding-bottom: 138px;
    padding-top: 40px;
    padding: 60px 0;
    position: relative;
    flex-wrap: wrap;
    width: 100%;
    align-items: center;
    margin: auto;

    @media (max-width: 768px) {
        margin: auto;
        min-height: 0px;
    }
`;

const Hero = styled.div`
    width: 100%;
    h1 {
        padding-bottom: 0;
        width: 55%;
        font-size: 53px;
        color: rgb(1,1,1, 0.6);
        font-weight: 200;
        line-height: 70px;

        @media (max-width: 768px) {
            text-align: center;
            font-size: 20px;
            width: 100%;
            width: 100%;
            line-height: 2;
        }
    }

    img {
        /*z-index: -1;*/
        width: 700px;
        height: 670px;
        position: absolute;
        bottom: -2px;
        right: -150px;

        @media (max-width: 768px) {
            top: 230px;
            width: initial;
            position: initial;
            height: initial;
        }
    }
`;

const Form = styled.div`
    margin-top: 100px;
    width: 408px;
    @media (max-width: 768px) {
        margin-top: 20px;
    }
`;

const Google = styled.button`
    display: flex;
    justify-content: center;
    background-color: #fff;
    align-items: center;
    height: 56px;
    width: 100%;
    border-radius: 0;
    box-shadow: inset 0 0 0 1px rgba(0 0 0 / 60%), inset 0 0 0 2px rgb(0 0 0 / 0%) inset 0 0 0 1px rgb(0 0 0 / 0%);
    vertical-align: middle;
    z-index: 0;
    transition-duration: 167ms;
    font-size: 20px;
    color: rgba(0, 0 , 0, 0.6);
    position: relative;

    &:hover {
        background-color: rgba(207, 207, 207, 0.25);
        color: rgba(0, 0, 0, 0.75);
        cursor: pointer;
    }
`;

const mapStateToProps = (state) => {
    return {
        user: state.userState.user,
    };
};

const mapDispatchToProps = (dispatch) => ({
    signIn: () => dispatch(signInAPI()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Login);
